import React, {useState} from "react";
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

import { useQuery, useMutation } from "@apollo/client";
import { getAuthors } from "../graphql-client/queries";
import { addSingleBook } from "../graphql-client/mutations";

const Forms = () => {
    const [newBook, setNewBook] = useState({
        name: '',
        genre: '',
        authorId: ''
    })

    const {name, genre, authorId} = nam

    const onInputChange = event => {
        setNewBook({
            ...newBook,
            [event.target.name]: event.target.value
        })
    }

    const onSubmit = event => {
        event.preventDefault()
        addBook({
            variables: {
                name: newBook.name,
                genre: newBook.genre,
                authorId: newBook.authorId
            }
        })
    }

    // GraphQL operations
    const {loading, error, data} = useQuery(getAuthors)
    
    const {addBook, dataMutation} = useMutation(addSingleBook)

    console.log(dataMutation)

    return <Row>
        <Col>
            <Form  onSubmit={onSubmit}>
                <Form.Group>
                    <Form.Control type="text" placeholder="Book name" name="name" onChange={onInputChange} value={newBook.name} />
                </Form.Group>
                <Form.Group>
                    <Form.Control type="text" placeholder="Book genre" name="genre" onChange={onInputChange} value={newBook.genre} />
                </Form.Group>
                <Form.Group>
                    {loading ? (<p>Loading authors...</p>) : 
                    (<Form.Control as='select' name="authorId" onChange={onInputChange} value={newBook.authorId}>
                        <option value='' disabled>Select author</option>
                        {data.authors.map(author => (
                            <option key={author.id} value={author.id}>{author.name}</option>
                        ))}
                    </Form.Control>)}
                    
                </Form.Group>
                <Button className="float-right" variant="info" type="submit">Add book</Button>
            </Form>
        </Col>
        <Col>
        <Form>
                <Form.Group className="invisible">
                    <Form.Control />
                </Form.Group>
                <Form.Group>
                    <Form.Control type="text" placeholder="Author name" />
                </Form.Group>
                <Form.Group>
                    <Form.Control type="number" placeholder="Author age" />
                </Form.Group>
                <Button className="float-right" variant="info" type="submit">Add author</Button>
            </Form>
        </Col>
    </Row>
}

export default Forms
