import React, {useState} from "react";
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

import { useMutation } from "@apollo/client";
import { getAuthors } from "../graphql-client/queries";
import { addSingleAuthor } from "../graphql-client/mutations";

const AuthorForm = () => {
    const [newAuthor, setNewAuthor] = useState({
        name: '',
        genre: '',
        authorId: ''
    })

    const {name, genre, authorId} = newAuthor

    const onInputChange = event => {
        setNewAuthor({
            ...newAuthor,
            [event.target.name]: event.target.value
        })
    }

    const onSubmit = event => {
        event.preventDefault()

        addBook({
            variables: {name,genre,authorId},
            refetchQueries: [{query: getBooks}]
        })

        setNewAuthor({name: '', genre: '', authorId: ''})
    }

    // GraphQL operations
    const {loading, error, data} = useQuery(getAuthors)
    
    const [addBook, dataMutation] = useMutation(addSingleBook)

    // console.log(dataMutation)

    return (
        <Form>
                <Form.Group className="invisible">
                    <Form.Control />
                </Form.Group>
                <Form.Group>
                    <Form.Control type="text" placeholder="Author name" />
                </Form.Group>
                <Form.Group>
                    <Form.Control type="number" placeholder="Author age" />
                </Form.Group>
                <Button className="float-right" variant="info" type="submit">Add author</Button>
            </Form>
    )
}

export default AuthorForm
