import { useQuery } from "@apollo/client";
import React, {Fragment} from "react";
import Card from 'react-bootstrap/Card'
import { getSingleBook } from "../graphql-client/queries";

const BookDetails = ({bookId}) => {
    const {loading, error, data} = useQuery(getSingleBook, {
        variables: {
            id: bookId
        }
    })

    if(loading) return <p>Loading book details...</p>
    if(bookId !== null && error) <p>Error loading book details!</p>

    const book = !loading && !error ? data.book : null

    return (
        <Card bg='info' text="white" className="shadow">
            <Card.Body>
                {
                    book === null ? <Card.Text>Select book</Card.Text> :
                    <Fragment>
                        <Card.Title>{book}</Card.Title>
                        <Card.Subtitle>Phong su</Card.Subtitle>
                        <p>Vu Trong Phung</p>
                        <p>Age: 90</p>
                        <p>All books by this author</p>
                        <ul>
                            <li>Ky nghe lay tay</li>
                            <li>So do</li>
                        </ul>
                    </Fragment>
                }
            </Card.Body>
        </Card>
    )
}

export default BookDetails
