import { gql } from "@apollo/client";

const addSingleBook = gql`
    mutation addSingleBookMutation($name: String, $genre: String, $authorId: ID!) {
        createBook(name: $name, genre: $genre, authorId: $authorId) {
            id
            name
        }
    }
`

const addSingleAuthor = gql`
    mutation addSingleAuthorMutation($name: String, $age: Number) {
        createAuthor(name: $name, genre: $genre, authorId: $authorId) {
            id
            name
        }
    }
`

export {addSingleBook}
