import {gql} from '@apollo/client'

const getBooks = gql`
    query getBooksQuery {
        books {
            name
            id
        }
    }
`

const getSingleBook = gql`
    query getSingleBookQuery {
        book(id: $id)
    }
`

export {getBooks}
